class Solution {public:	int rob(vector<int>& nums) {		std::vector<int> dp;		int iSize = (int)nums.size();		int p2 = iSize > 0 ? nums[0] : 0, p1 = iSize > 1 ? std::max(nums[0], nums[1]) : 0;		int iResult = std::max(p2, p1);		for (int i = 2; i < (int)nums.size(); ++i) {			p2 = std::max(p2 + nums[i], p1);			iResult = std::max(iResult, p2);			std::swap(p2, p1);		}		return iResult;	}};