/** * Definition for singly-linked list. * struct ListNode { *     int val; *     ListNode *next; *     ListNode(int x) : val(x), next(NULL) {} * }; */class Solution {public:	ListNode* reverseList(ListNode* head) {		ListNode* pkLastNode = nullptr;		for (; head;) {			ListNode* tmp = head->next;			head->next = pkLastNode;			pkLastNode = head;			head = tmp;		}		return pkLastNode;	}};